messages.content:
[
  {
    "name": "Stubb",
    "physical_description": null,
    "emotional_state": "Jolly",
    "personality_description": "Likes cigars, doesn't like smoking dirty pipes"
  },
  {
    "name": "King-Post",
    "physical_description": "Described as 'little'",
    "emotional_state": null,
    "personality_description": "Seems practical, values gold, prefers cigars over dirty pipes"
  },
  {
    "name": "Manxman",
    "physical_description": "Old, voice like an old worn-out coffee-mill",
    "emotional_state": null,
    "personality_description": "Experienced, has knowledge of signs and marks, taught by the old witch in Copenhagen"
  },
  {
    "name": "Queequeg",
    "physical_description": "Covered in tattoos, looks like the signs of the Zodiac",
    "emotional_state": null,
    "personality_description": "Possibly has knowledge of or interest in astronomy, confused by the doubloon"
  },
  {
    "name": "Fedallah",
    "physical_description": "Ghost-like, tail coiled out of sight, oakum in the toes of his pumps",
    "emotional_state": null,
    "personality_description": "Possibly a fire worshipper, recognized a sun on the coin"
  },
  {
    "name": "Pip",
    "physical_description": "Described as having an 'unearthly idiot face'",
    "emotional_state": null,
    "personality_description": "Is viewed as being 'half horrible', appears to be studying Murray's Grammar and repeating phrases from it"
  }
]

[
  {
    "name": "Dorothy",
    "physical_description": "A little girl wearing a pretty gingham dress with white and blue checks",
    "emotional_state": "Hungry, determined to reach Emerald City",
    "personality_description": "Caring, brave, resourceful"
  },
  {
    "name": "Toto",
    "physical_description": "Small dog with black eyes and wagging tail",
    "emotional_state": null,
    "personality_description": "Loyal companion to Dorothy, keen on exploring"
  },
  {
    "name": "Munchkins",
    "physical_description": "Short people with pointed blue hats, dressed in blue clothes and wearing yellow shoes",
    "emotional_state": "Joyful, grateful to Dorothy for freeing them from bondage",
    "personality_description": "Curious, friendly, hardworking farmers"
  },
  {
    "name": null,
    "physical_description": "The land is described as 'pretty' and the houses are painted in a dainty blue color with big dome-like roofs. Fences line both sides of the yellow brick road that Dorothy walks on.",
    "emotional_state": null,
    "personality_description": "Vibrant, friendly, peaceful"
  }
]
